import React, { useState, useEffect } from "react";
import "../../../src/App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { usebookingInput } from "bookingInput";
import "../../style.css";

function BookingInput({ setBookings }) {
  const [bookingInput, setBookingInput] = useState({
    firstName: "",
    surname: "",
    title: "",
    email: "",
    roomId: "",
    checkInDate: "",
    checkOutDate: "",
  });

  return (
    <div>
      <h1>Input Booking</h1>
      <form onSubmit={bookingInput.handleSubmit} className="inputForm">
        <label htmlFor="title">Title</label>
        <input
          type="text"
          id="title"
          name="title"
          onChange={bookingInput.handleChange}
          value={bookingInput.title}
          placeholder="Title"
        />
        <label htmlFor="firstName">First Name</label>
        <input
          type="text"
          id="FirstName"
          name="firstName"
          onChange={bookingInput.handleChange}
          value={bookingInput.firstName}
          placeholder="First Name"
        />
        <label htmlFor="surname">Surname</label>
        <input
          type="text"
          id="surname"
          name="surname"
          onChange={bookingInput.handleChange}
          value={bookingInput.surname}
          placeholder="Last Name"
        />

        <label htmlFor="email">email</label>
        <input
          type="text"
          id="email"
          name="email"
          onChange={bookingInput.handleChange}
          value={bookingInput.email}
          placeholder="email"
        />
        <label htmlFor="roomId">Room Id</label>
        <input
          type="text"
          id="roomId"
          name="roomId"
          onChange={bookingInput.handleChange}
          value={bookingInput.roomId}
          placeholder="Room Id"
        />
        <label htmlFor="CheckinDate">Checkin Date</label>
        <input
          type="text"
          id="checkinDate"
          name="checkinDate"
          onChange={bookingInput.handleChange}
          value={bookingInput.checkinDate}
          placeholder="Checkin"
        />
        <label htmlFor="CheckOutDate">Check Out Date</label>
        <input
          type="text"
          id="checkOutDate"
          name="checkOutDate"
          onChange={bookingInput.handleChange}
          value={bookingInput.checkOutDate}
          placeholder="Check Out"
        />
        <button>Submit</button>
      </form>
    </div>
  );
}

